            ('TOKEN_DIV', r'/'),
            ('TOKEN_LPAREN', r'\('),
            ('TOKEN_RPAREN', r'\)'),
            ('TOKEN_SKIP', r'\s+'),
            ('MISMATCH', r'.'),
