class Lexer:
    def __init__(self, text):
        self.text = text
        self.pos = 0
        self.tokens = []
        self.tokenize()

    def tokenize(self):
        # Regex patterns
        token_specs = [
            ('TOKEN_LET', r'let'),
            ('TOKEN_PRINT', r'print'),
            ('TOKEN_SEMI', r';'),
            ('TOKEN_EQ', r'='),
            ('TOKEN_PLUS', r'\+'),
            ('TOKEN_MINUS', r'\-'),
            ('TOKEN_MUL', r'\*'),
            ('TOKEN_DIV', r'\/'),
            ('TOKEN_MOD', r'%'),
            ('TOKEN_LPAREN', r'\('),
            ('TOKEN_RPAREN', r'\)'),
            ('TOKEN_NUMBER', r'\d+'),
            ('TOKEN_ID', r'[a-zA-Z_]\w*'),
            ('TOKEN_WS', r'\s+'),
            ('MISMATCH', r'.'),
        ]

        group_map = {'TOKEN_LET': 'LET', 'TOKEN_PRINT': 'PRINT', 'TOKEN_SEMI': 'SEMI', 'TOKEN_EQ': 'EQ', 'TOKEN_PLUS': 'PLUS', 'TOKEN_MINUS': 'MINUS', 'TOKEN_MUL': 'MUL', 'TOKEN_DIV': 'DIV', 'TOKEN_MOD': 'MOD', 'TOKEN_LPAREN': 'LPAREN', 'TOKEN_RPAREN': 'RPAREN', 'TOKEN_NUMBER': 'NUMBER', 'TOKEN_ID': 'ID', 'TOKEN_WS': 'WS'}

        # Compile regex
        tok_regex = '|'.join('(?P<%s>%s)' % pair for pair in token_specs)
        get_token = re.compile(tok_regex).match

        skipped_tokens = {'WS'}
        line_num = 1
        line_start = 0
        mo = get_token(self.text)
        while mo is not None:
            kind = mo.lastgroup
            value = mo.group(kind)
            if kind == 'MISMATCH':
                raise ParseError(f'Unexpected character {value!r} on line {line_num}')
            
            # Map back to token type
            token_type = group_map.get(kind, kind)
            
            if token_type in skipped_tokens:
                pass
            else:
                self.tokens.append(Token(token_type, value, line_num, mo.start() - line_start))
            
            # Update position
            pos = mo.end()
            mo = get_token(self.text, pos)
            if pos == len(self.text): break

